CREATE TABLE aut_institution (INS_ID varchar(50) COLLATE utf8_bin NOT NULL, INS_CODE varchar(50) COLLATE utf8_bin, INS_NAME varchar(255) COLLATE utf8_bin, INS_ENGNAME varchar(255) COLLATE utf8_bin, INS_TWNAME varchar(255) COLLATE utf8_bin, INS_DESCRIBE varchar(255) COLLATE utf8_bin, IS_START varchar(1) COLLATE utf8_bin DEFAULT 'Y', SYSTEM_DATETIME varchar(50) COLLATE utf8_bin, INS_SUPER_ID varchar(255) COLLATE utf8_bin, INS_ORDER int, PRIMARY KEY (INS_ID)) ENGINE=InnoDB DEFAULT CHARSET=utf8;
CREATE TABLE aut_institution_role (INS_ID varchar(50) COLLATE utf8_bin NOT NULL, ROLE_ID varchar(50) COLLATE utf8_bin NOT NULL, SYSTEM_DATETIME varchar(50) COLLATE utf8_bin, PRIMARY KEY (INS_ID, ROLE_ID)) ENGINE=InnoDB DEFAULT CHARSET=utf8;
CREATE TABLE aut_institution_user (INS_ID varchar(50) COLLATE utf8_bin NOT NULL, USER_ID varchar(50) COLLATE utf8_bin NOT NULL, SYSTEM_DATETIME varchar(50) COLLATE utf8_bin, PRIMARY KEY (INS_ID, USER_ID)) ENGINE=InnoDB DEFAULT CHARSET=utf8;
CREATE TABLE aut_menu (ID varchar(50) COLLATE utf8_bin NOT NULL, NAME varchar(255) COLLATE utf8_bin, ENGNAME varchar(255) COLLATE utf8_bin, TWNAME varchar(255) COLLATE utf8_bin, URL varchar(100) COLLATE utf8_bin, ISVALID char(1) COLLATE utf8_bin, CREATE_DATE varchar(50) COLLATE utf8_bin, TOPMENUID varchar(50) COLLATE utf8_bin, SORT_ORDER int DEFAULT '0', PRIMARY KEY (ID)) ENGINE=InnoDB DEFAULT CHARSET=utf8;
CREATE TABLE aut_operation (OPE_ID varchar(50) COLLATE utf8_bin NOT NULL, OPE_CODE varchar(50) COLLATE utf8_bin NOT NULL, OPE_NAME varchar(50) COLLATE utf8_bin, OPE_ENGNAME varchar(50) COLLATE utf8_bin, OPE_TWNAME varchar(50) COLLATE utf8_bin, SYSTEM_DATETIME varchar(50) COLLATE utf8_bin, PRIMARY KEY (OPE_ID, OPE_CODE)) ENGINE=InnoDB DEFAULT CHARSET=utf8;
CREATE TABLE aut_role (ROLE_ID varchar(50) COLLATE utf8_bin NOT NULL, ROLE_CODE varchar(255) COLLATE utf8_bin, ROLE_NAME varchar(255) COLLATE utf8_bin, ROLE_ENGNAME varchar(255) COLLATE utf8_bin, ROLE_TWNAME varchar(255) COLLATE utf8_bin, IS_START char(1) COLLATE utf8_bin, SYSTEM_DATETIME varchar(50) COLLATE utf8_bin, ROLE_SUPER_ID varchar(50) COLLATE utf8_bin, ROLE_ORDER int DEFAULT '0', PRIMARY KEY (ROLE_ID)) ENGINE=InnoDB DEFAULT CHARSET=utf8;
CREATE TABLE aut_role_menu_operation (ROLE_ID varchar(50) COLLATE utf8_bin NOT NULL, MENU_ID varchar(50) COLLATE utf8_bin NOT NULL, OPE_ID varchar(50) COLLATE utf8_bin NOT NULL, PRIMARY KEY (ROLE_ID, MENU_ID, OPE_ID)) ENGINE=InnoDB DEFAULT CHARSET=utf8;
CREATE TABLE aut_tempmenu (ID varchar(50) COLLATE utf8_bin NOT NULL, TOPMENUID varchar(50) COLLATE utf8_bin, TEXT varchar(255) COLLATE utf8_bin, PRIMARY KEY (ID)) ENGINE=InnoDB DEFAULT CHARSET=utf8;
CREATE TABLE aut_user (USER_ID varchar(50) COLLATE utf8_bin NOT NULL, USER_NAME varchar(255) COLLATE utf8_bin, USER_ENGNAME varchar(255) COLLATE utf8_bin, USER_TWNAME varchar(255) COLLATE utf8_bin, PASSWORD varchar(255) COLLATE utf8_bin, IS_START char(1) COLLATE utf8_bin, LAST_LOGIN_TIME varchar(50) COLLATE utf8_bin, SYSTEM_DATETIME varchar(50) COLLATE utf8_bin, PRIMARY KEY (USER_ID)) ENGINE=InnoDB DEFAULT CHARSET=utf8;
CREATE TABLE aut_user_role (USER_ID varchar(50) COLLATE utf8_bin NOT NULL, ROLE_ID varchar(50) COLLATE utf8_bin NOT NULL, SYSTEM_DATETIME varchar(50) COLLATE utf8_bin, PRIMARY KEY (USER_ID, ROLE_ID)) ENGINE=InnoDB DEFAULT CHARSET=utf8;
CREATE TABLE subtest (block_id varchar(20) DEFAULT '0' NOT NULL, sub_id varchar(20) DEFAULT '0' NOT NULL, title_no varchar(20), title_name varchar(20), type_id varchar(20), PRIMARY KEY (block_id, sub_id)) ENGINE=InnoDB DEFAULT CHARSET=utf8;
CREATE TABLE test (block_id varchar(20) COLLATE latin1_swedish_ci DEFAULT '0' NOT NULL, title_no varchar(20) COLLATE latin1_swedish_ci, title_name varchar(20) COLLATE latin1_swedish_ci, type_id varchar(20) COLLATE latin1_swedish_ci, PRIMARY KEY (block_id)) ENGINE=InnoDB DEFAULT CHARSET=utf8;
CREATE TABLE wf_cc_order (order_Id varchar(32) COMMENT '流程实例ID', actor_Id varchar(50) COMMENT '参与者ID', creator varchar(50) COMMENT '发起人', create_Time varchar(50) COMMENT '抄送时间', finish_Time varchar(50) COMMENT '完成时间', status tinyint(1) COMMENT '状态', INDEX IDX_CCORDER_ORDER (order_Id)) ENGINE=MyISAM DEFAULT CHARSET=utf8 COMMENT='抄送实例表';
CREATE TABLE wf_hist_order (id varchar(32) NOT NULL COMMENT '主键ID', process_Id varchar(32) NOT NULL COMMENT '流程定义ID', order_State tinyint(1) NOT NULL COMMENT '状态', creator varchar(50) COMMENT '发起人', create_Time varchar(50) NOT NULL COMMENT '发起时间', end_Time varchar(50) COMMENT '完成时间', expire_Time varchar(50) COMMENT '期望完成时间', priority tinyint(1) COMMENT '优先级', parent_Id varchar(32) COMMENT '父流程ID', order_No varchar(50) COMMENT '流程实例编号', variable varchar(2000) COMMENT '附属变量json存储', PRIMARY KEY (id), INDEX IDX_HIST_ORDER_PROCESSID (process_Id), INDEX IDX_HIST_ORDER_NO (order_No), INDEX FK_HIST_ORDER_PARENTID (parent_Id)) ENGINE=MyISAM DEFAULT CHARSET=utf8 COMMENT='历史流程实例表';
CREATE TABLE wf_hist_task (id varchar(32) NOT NULL COMMENT '主键ID', order_Id varchar(32) NOT NULL COMMENT '流程实例ID', task_Name varchar(100) NOT NULL COMMENT '任务名称', display_Name varchar(200) NOT NULL COMMENT '任务显示名称', task_Type tinyint(1) NOT NULL COMMENT '任务类型', perform_Type tinyint(1) COMMENT '参与类型', task_State tinyint(1) NOT NULL COMMENT '任务状态', operator varchar(50) COMMENT '任务处理人', create_Time varchar(50) NOT NULL COMMENT '任务创建时间', finish_Time varchar(50) COMMENT '任务完成时间', expire_Time varchar(50) COMMENT '任务期望完成时间', action_Url varchar(200) COMMENT '任务处理url', parent_Task_Id varchar(32) COMMENT '父任务ID', variable varchar(2000) COMMENT '附属变量json存储', PRIMARY KEY (id), INDEX IDX_HIST_TASK_ORDER (order_Id), INDEX IDX_HIST_TASK_TASKNAME (task_Name), INDEX IDX_HIST_TASK_PARENTTASK (parent_Task_Id)) ENGINE=MyISAM DEFAULT CHARSET=utf8 COMMENT='历史任务表';
CREATE TABLE wf_hist_task_actor (task_Id varchar(32) NOT NULL COMMENT '任务ID', actor_Id varchar(50) NOT NULL COMMENT '参与者ID', INDEX IDX_HIST_TASKACTOR_TASK (task_Id)) ENGINE=MyISAM DEFAULT CHARSET=utf8 COMMENT='历史任务参与者表';
CREATE TABLE wf_order (id varchar(32) NOT NULL COMMENT '主键ID', parent_Id varchar(32) COMMENT '父流程ID', process_Id varchar(32) NOT NULL COMMENT '流程定义ID', creator varchar(50) COMMENT '发起人', create_Time varchar(50) NOT NULL COMMENT '发起时间', expire_Time varchar(50) COMMENT '期望完成时间', last_Update_Time varchar(50) COMMENT '上次更新时间', last_Updator varchar(50) COMMENT '上次更新人', priority tinyint(1) COMMENT '优先级', parent_Node_Name varchar(100) COMMENT '父流程依赖的节点名称', order_No varchar(50) COMMENT '流程实例编号', variable varchar(2000) COMMENT '附属变量json存储', version int(3) COMMENT '版本', PRIMARY KEY (id), INDEX IDX_ORDER_PROCESSID (process_Id), INDEX IDX_ORDER_NO (order_No), INDEX FK_ORDER_PARENTID (parent_Id)) ENGINE=MyISAM DEFAULT CHARSET=utf8 COMMENT='流程实例表';
CREATE TABLE wf_process (id varchar(32) NOT NULL COMMENT '主键ID', name varchar(100) COMMENT '流程名称', display_Name varchar(200) COMMENT '流程显示名称', type varchar(100) COMMENT '流程类型', instance_Url varchar(200) COMMENT '实例url', state tinyint(1) COMMENT '流程是否可用', content longblob COMMENT '流程模型定义', version int(2) COMMENT '版本', create_Time varchar(50) COMMENT '创建时间', creator varchar(50) COMMENT '创建人', PRIMARY KEY (id), INDEX IDX_PROCESS_NAME (name)) ENGINE=MyISAM DEFAULT CHARSET=utf8 COMMENT='流程定义表';
CREATE TABLE wf_surrogate (id varchar(32) NOT NULL COMMENT '主键ID', process_Name varchar(100) COMMENT '流程名称', operator varchar(50) COMMENT '授权人', surrogate varchar(50) COMMENT '代理人', odate varchar(64) COMMENT '操作时间', sdate varchar(64) COMMENT '开始时间', edate varchar(64) COMMENT '结束时间', state tinyint(1) COMMENT '状态', PRIMARY KEY (id), INDEX IDX_SURROGATE_OPERATOR (operator)) ENGINE=MyISAM DEFAULT CHARSET=utf8 COMMENT='委托代理表';
CREATE TABLE wf_task (id varchar(32) NOT NULL COMMENT '主键ID', order_Id varchar(32) NOT NULL COMMENT '流程实例ID', task_Name varchar(100) NOT NULL COMMENT '任务名称', display_Name varchar(200) NOT NULL COMMENT '任务显示名称', task_Type tinyint(1) NOT NULL COMMENT '任务类型', perform_Type tinyint(1) COMMENT '参与类型', operator varchar(50) COMMENT '任务处理人', create_Time varchar(50) COMMENT '任务创建时间', finish_Time varchar(50) COMMENT '任务完成时间', expire_Time varchar(50) COMMENT '任务期望完成时间', action_Url varchar(200) COMMENT '任务处理的url', parent_Task_Id varchar(32) COMMENT '父任务ID', variable varchar(2000) COMMENT '附属变量json存储', version tinyint(1) COMMENT '版本', PRIMARY KEY (id), INDEX IDX_TASK_ORDER (order_Id), INDEX IDX_TASK_TASKNAME (task_Name), INDEX IDX_TASK_PARENTTASK (parent_Task_Id)) ENGINE=MyISAM DEFAULT CHARSET=utf8 COMMENT='任务表';
CREATE TABLE wf_task_actor (task_Id varchar(32) NOT NULL COMMENT '任务ID', actor_Id varchar(50) NOT NULL COMMENT '参与者ID', INDEX IDX_TASKACTOR_TASK (task_Id)) ENGINE=MyISAM DEFAULT CHARSET=utf8 COMMENT='任务参与者表';
